import {useState} from "react";


function App() {

    // 반복문으로 같은 html 반복생성하는 법
    // 그 전에 map()함수에대해서 알아보자.모든 어레이 자료뒤에다가 붙일수있음.map함수를 쓸때는 콜백함수를 넣어야함.소괄호안에들어가는 함수를 콜백함수라함. function
    // map함수의 기능은 array자료 갯수만큼 함수안의 코드를 실행해줌.
    // 함수의 파라미터는 array안에 있던 자료임.코드가 반복실행되면서 1이되고 2가되고 3이된다.
    // [1,2,3].map(function (a){
    //     console.log(a)
    // })

    // return에 뭐적으면 array로 담아줌
        [1,2,3].map(function (a){
        return '1233211'
    })

    let post = '강남 우동 맛집';
    let [title, settitle] = useState(['남자 코트 추천', '강남 우동맛집', '파이썬독학'])
    let [like, setlike] = useState([0,0,0]);
    let [modal, setModal] = useState(false);

    return (
        <div className="App">
            <div className='black-nav'>
                <h4>블로그임 </h4>
            </div>

            <button onClick={() => {
                let copy = [...title];
                copy[0] = '여자코트 추천';
                settitle(copy)
            }}> 수정버튼
            </button>


            {/*<div className="list">*/}
            {/*    <h4>{title[0]} <span onClick={() => {*/}
            {/*        setlike(like + 1)*/}
            {/*    }}>👍</span>{like}</h4>*/}
            {/*    <p>2월 17일 발행</p>*/}
            {/*</div>*/}
            {/*<div className="list">*/}
            {/*    <h4>{title[1]}</h4>*/}
            {/*    <p>2월 17일 발행</p>*/}
            {/*</div>*/}
            {/*<div className="list">*/}
            {/*    <h4 onClick={()=>{setModal(!modal )}}>{title[2]}</h4>*/}
            {/*    <p>2월 17일 발행</p>*/}
            {/*</div>*/}
            {/*{*/}
            {/*    modal == true ? <Modal></Modal> : null*/}
            {/*}*/}

            {/*{*/}
            {/* [1,2,3].map(function (){*/}
            {/*     return <div>안녕</div>*/}
            {/* })*/}
            {/*}                                   /!*map()으로 같은 html반복생성하는 법*!/*/}
            {/*========================================================================================================================


            */}
            {/*{*/}
            {/*    [1,2,3].map(function () {     /!*} 실제글이 100개면 어쩔거임? 때문에 실제 블로그 글 갯수만큼 html생성하는 방법을 알아보자./*!/*/}
            {/*        return*/}
            {/*        <div className="list">*/}
            {/*            <h4>{title[1]}</h4>*/}
            {/*            <p>2월 17일 발행</p>*/}
            {/*        </div>*/}
            {/*    })*/}
            {/*}*/}

        {/*    {*/}
        {/*    title.map(function (){    /!*이렇게 title state를 그대로 가져오면 title도 array니까 실제 데이터개수에 맞게 알아서 반영해줌.*/}
        {/*                                    문제는 같은글만 3개보임.{title[1]}로 하드코딩됐기때문에 각각 다른게 보이려면 어떻게해야할까?*/}
        {/*                                    ()파라미터를 신경쓰면 될것같다.파라미터는 array안에있던 데이터랬음.*/}
        {/*                                                                                                    *!/*/}
        {/*        return (*/}
        {/*            <div className="list">*/}
        {/*                <h4>{title[1]}</h4>*/}
        {/*                <p>2월 17일 발행</p>*/}
        {/*            </div>*/}

        {/*        )*/}
        {/*    })*/}
        {/*}*/}

            {
                title.map(function (a,i){
                    return (
                        <div className="list">
                            <h4>{title[i]}
                                <span onClick={() =>{
                                      let copy = [...like];
                                      copy[i] = copy[i] + 1;
                                      setlike(copy)
                            }}>👍</span>{like[i]}</h4>
                            <p>2월 17일 발행</p>
                        </div>


                    )
                })
            }
            {/* 파라미터 안에 a를 넣어주고 {title[1]} 를 {a} 로 데이터 바인딩해주면 array안에있는 데이터들이 하나씩 표현이됨.
                                                        두개까지 작명가능 i를 하나 a옆에 만들고 i는 반복문 돌때마다 0부터 1씩 증가하는 정수이기때문에
                                                        가시성이 있게 title[i]로 바꿔주면 될것같다.
                                                        정리하자면
                                                        1.왼쪽 array 자료만큼 내부코드 실행해줌.
                                                        2.return 오른쪽에 있는걸 array로 담아줌
                                                        3.유용한 파라미터 2개 사용가능
                                                        문제는 모든 좋아요가 동시에 같이 증가하는 부분 일단 like state를 array로 만들어 [0,0,0] 으로 변경해줌
                                                          <span onClick={() =>{
                                      let copy = [...like];
                                      copy[i] = copy[i] + 1;
                                      setlike(copy)
                            }}>👍</span>{like[i]}</h4>
                            이렇게 해주면 된다.

                                                                                                           */}

        </div>

    );
}


function Modal() {
    return (
        <div className="modal">
        <h4>제목</h4>
            <p>날짜</p>
            <p>상세내용</p>
        </div>
    )
}


export default App;
